import React, { Component } from 'react'
import axios from 'axios'

class ConsumoApi extends Component {
    state={
        pokemonList:[]
    }

    componentDidMount = () => {
        this.getData()
    }

    getData = () => {
        axios.get('https://pokeapi.co/api/v2/pokemon').then(
            (response) => {
                const { data } = response;
                let { pokemonList } = this.state
                pokemonList = data.results
                this.setState({
                    pokemonList
                });
            },
            (error) => {
                console.log("Ocurrio un error");
            }
        );
    };
    render() {
        const { pokemonList } = this.state
        console.log(pokemonList, 'pokemonList')
        return (
            <>
                <div className="row justify-content-center">
                    <div className="col-md-10">
                        <div className="member" data-aos="fade-up" data-aos-delay="400">
                            <div className="member-info">
                                <h4>Lista de pokemones</h4>
                                    <span className="mb-3">{pokemonList.length} pokemones obtenidos </span>

                                <table className="table table-responsive-sm table-hover">
                                    <thead className="text-center">
                                        <tr>
                                            <th className="w-20"><h4>NO.</h4></th>
                                            <th className="w-40"><h4>NOMBRE</h4></th>
                                            <th className="w-40"><h4>MÁS INFORMACIÓN</h4></th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            pokemonList.map((pokemon, key)=>{
                                                return(
                                                    <tr key = { key }>
                                                        <td>{key+1}</td>
                                                        <td className="text-capitalize">{pokemon.name}</td>
                                                        <td>
                                                            <button type="button" className="btn btn-outline-success btn-sm"><strong><i className="fa fa fa-pencil"></i></strong></button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

export default ConsumoApi;
