import React, { Component } from 'react'
import axios from 'axios'
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import SVG from 'react-inlinesvg'
const MySwal = withReactContent(Swal)

class ConsumoApi extends Component {
    state = {
        pokemonList:[],
        pokemonInfo:[]
    }

    componentDidMount = () => {
        this.getPokemonList()
    }

    getPokemonList = () => {
        axios.get('https://pokeapi.co/api/v2/pokemon').then(
            (response) => {
                const { data } = response;
                let { pokemonList } = this.state
                pokemonList = data.results
                this.setState({
                    pokemonList
                });
            },
            (error) => {
                console.log("Ocurrio un error");
            }
        );
    };
    getInfoPoxemon = (key) => {
        axios.get(`https://pokeapi.co/api/v2/pokemon/${key}`).then(
            (response) => {
                const { data } = response;
                this.setState({
                    pokemonInfo: data
                });
                this.infoSwal()
            },
            (error) => {
                console.log("Ocurrio un error");
            }
        );
    }
    infoSwal (){
        const { pokemonInfo } = this.state
        console.log(pokemonInfo, 'pokemonInfo')
        MySwal.fire({
            html:  <div>
                <div className="text-capitalize font-weight-bold mb-4 text-black">{pokemonInfo.name}</div>
                <div className="row mx-0">
                    <div className="col-md-4">
                        <SVG width='100%' height='auto' loader={<span className="text-secondary font-weight-light font-size-lg">Cargando imagen</span>} src={pokemonInfo.sprites.other.dream_world.front_default}/>
                    </div>
                    <div className="col-md-8">
                        <div>
                            <table className="table font-size-lg">
                                <thead className="table-secondary">
                                    <tr>
                                        <th scope="col" colSpan="2" className="text-black">Información general</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <th scope="row">Habilidades</th>
                                        <td>Mark</td>
                                    </tr>
                                    <tr>
                                        <th scope="row">Tipos</th>
                                        <td>
                                            <div className="w-max-content">
                                            {
                                                pokemonInfo.types.map((type, key)=>{
                                                    return(
                                                        <div>• {type.type.name} <br/></div>
                                                    )
                                                })
                                            }
                                            </div>
                                        </td>
                                    </tr>
                                    <tr>
                                        <th scope="row">Experiencia</th>
                                        <td>{pokemonInfo.base_experience}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>,
            showConfirmButton: false
        })
    }
    toAbsoluteUrl = pathname => process.env.PUBLIC_URL + pathname;
    render() {
        const { pokemonList } = this.state
        return (
            <>
                <div className="row justify-content-center">
                    <div className="col-md-10">
                        <div className="member" data-aos="fade-up" data-aos-delay="400">
                            <div className="member-info">
                                <h4>Lista de pokemones</h4>
                                    <span className="mb-3">{pokemonList.length} pokemones obtenidos </span>
                                <table className="table table-responsive-sm table-hover">
                                    <thead className="text-center">
                                        <tr>
                                            <th className="w-20"><h4>NO.</h4></th>
                                            <th className="w-40"><h4>NOMBRE</h4></th>
                                            <th className="w-40"><h4>MÁS INFORMACIÓN</h4></th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            pokemonList.map((pokemon, key)=>{
                                                return(
                                                    <tr key = { key }>
                                                        <td>{key+1}</td>
                                                        <td className="text-capitalize">{pokemon.name}</td>
                                                        <td>
                                                            <button type="button" className="btn btn-outline-primary btn-sm p-1" onClick={() => { this.getInfoPoxemon(key+1) }}>
                                                                <i className="las la la-search font-size-lg"></i>
                                                            </button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

export default ConsumoApi;
